import React, { useState } from "react";

const Sidebar = ({ minimized, setMinimized }) => {
  const changeSidebarSize = () => {
    setMinimized(!minimized);
  };
  return (
    <>
      <button
        className={`${
          minimized ? "absolute top-1 left-1" : "hidden"
        } lg:hidden curo`}
      >
        <span
          onClick={changeSidebarSize}
          className="material-symbols-outlined rotate-[90deg]"
        >
          expand_less
        </span>
      </button>
      <div
        className={`bg-zinc-100 absolute lg:relative h-full w-3/4 sm:w-2/3 md:w-1/2 ${
          minimized ? "hidden lg:w-1/12 xl:w-[5%]" : "lg:w-3/12 xl:w-2/12"
        } `}
      >
        <div className="flex flex-col h-full">
          <div className="bg-purple-600 sm:bg-blue-600 md:bg-emerald-600 lg:bg-orange-700 xl:bg-yellow-500 text-white font-semibold h-[5px]">
            a
          </div>

          <div
            className={`${
              minimized ? "hidden" : "flex"
            } h-full lg:flex flex-row justify-between`}
          >
            <div className="header flex flex-row h-20 w-full justify-between items-center px-2">
              <img
                src={minimized ? "v_logo_stripped.png" : "h_logo.png"}
                className="h-14"
              />
              <button onClick={changeSidebarSize}>
                <span
                  className={`material-symbols-outlined ${
                    minimized ? "rotate-[90deg]" : "rotate-[270deg]"
                  }`}
                >
                  expand_less
                </span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
