import React, { useState, useContext } from "react";
import axios from "axios";
import { TokenContext } from "../TokenContext";

const LoginPage = () => {
  const [email, setEmail] = useState("teste@gmail.com");
  const [password, setPassword] = useState("1234");
  const { setTokens } = useContext( TokenContext );

  const handleLogin = async (event) => {
    event.preventDefault();
    const response = await axios.post("http://localhost:5000/login", {
      email: email,
      password: password,
    });
    
    // Assuming the response contains the tokens in the data property
    const accessToken  = response.data.access_token;
    const refreshToken = response.data.refresh_token;
    
    // Set the tokens in the context
    setTokens({ accessToken, refreshToken });

    const { tokens }  = useContext( TokenContext );

  };

  return (
    <>
      <div className="w-full flex items-center justify-center h-[100vh] bg-gradient-to-tr from-green-300 via-blue-500 to-purple-600">
        <div className="w-full bg-white rounded-lg shadow  md:mt-0 sm:max-w-md xl:p-0 ">
          <div className="px-6 py-8 space-y-4 md:space-y-6 sm:px-8 sm:py-12">
            <h1 className="text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl ">
              Sign in to your account
            </h1>
            <form className="space-y-4 md:space-y-6" action="#">
              <div>
                <label
                  htmlFor="email"
                  className="block mb-2 text-sm font-medium text-gray-900 "
                >
                  Your email
                </label>
                <input
                  type="email"
                  name="email"
                  id="email"
                  className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5"
                  placeholder="leandro@gmail.com"
                  required
                  value={email}
                  onChange={(event) => setEmail(event.target.value)}
                />
              </div>
              <div>
                <label
                  htmlFor="password"
                  className="block mb-2 text-sm font-medium text-gray-900 "
                >
                  Password
                </label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  placeholder="••••••••"
                  className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5   "
                  required=""
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                />
              </div>
              <div className="flex items-center justify-between">
                <div className="flex items-start">
                  <div className="flex items-center h-5">
                    <input
                      id="remember"
                      aria-describedby="remember"
                      type="checkbox"
                      className="w-4 h-4 border border-gray-300 rounded bg-gray-50 focus:ring-3 focus:ring-primary-300"
                      required=""
                    />
                  </div>
                  <div className="ml-3 text-sm">
                    <label htmlFor="remember" className="text-gray-500 ">
                      Remember me
                    </label>
                  </div>
                </div>
                <a
                  href="#"
                  className="text-sm font-medium text-primary-600 hover:underline "
                >
                  Forgot password?
                </a>
              </div>
              <button
                type="submit"
                onClick={handleLogin}
                className="w-full text-white bg-gradient-to-tr from-green-300 via-blue-500 to-purple-600 hover:bg-primary-700 focus:ring-4 focus:outline-none focus:ring-primary-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center transition duration-500 ease-in-out transform hover:scale-[1.01] hover:shadow-lg"
              >
                {" "}
                Sign in
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginPage;
