import React from "react";

const SidebarItem = ({
  icon,
  title,
  minimized,
  setMinimized,
  activeComponent,
  setActiveComponent,
}) => {
  return (
    <div>
      <div onClick={setActiveComponent(title)} className={`flex flex-row items-center space-x-2 p-2 text-black font-medium rounded-md ${activeComponent===title} hover:bg-gray-200 hover:text-blue-600 duration-150 cursor-pointer`}>
        <span class="material-symbols-rounded">{icon}</span>
        {!minimized ? <span>{title}</span> : null}
      </div>
    </div>
  );
};

export default SidebarItem;
