import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { set } from 'date-fns';

const AtividadesComponent = ({ token, handleButtonClick }) => {
  const [data, setData] = useState(null);
  const [requestToken, setRequestToken] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      if (token) {
        setRequestToken(token);
        try {
          const response = await axios.post('http://localhost:5000/api/getatividades', {
            user_id: 'auth0|65999d197d1aba9a56a35600', // Replace with the actual user_id
          }, {
            headers: {
              Authorization: `Bearer ${requestToken}`, // Use the token here
            },
          });
          setData(response.data);
        } catch (error) {
          console.error('Error:', error.message);
        }
      }
    };
  
    if (token) {
      fetchData();
    }
  }, [token]);

  return (
    <>
      <button onClick={() => handleButtonClick("menu")}>Back to Menu</button>
      <div>Atividades Component </div>
      {data && <pre>{JSON.stringify(data, null, 2)}</pre>}
    </>
  )
}

export default AtividadesComponent
