import React from "react";

const SidebarItem = ({
  icon,
  title,
  minimized,
  setMinimized,
  activeComponent,
  setActiveComponent,
}) => {
  return (
    <div>
      <div
        onClick={() => setActiveComponent(title)}
        className={`transition-all duration-500 flex flex-row items-center space-x-2 p-2 text-black dark:text-neutral-300 font-medium dark:font-normal rounded-md cursor-pointer ${
          activeComponent === title
            ? "bg-gray-200 text-blue-600"
            : "hover:bg-gray-200 hover:text-blue-600 dark: duration-50"
        } ${minimized ? "justify-center" : ""} `}
      >
        <span class="material-symbols-rounded">{icon}</span>
        {!minimized ? <span>{title}</span> : null}
      </div>
    </div>
  );
};

export default SidebarItem;
