import React, { useState } from "react";

const Sidebar = () => {
  const [minimized, setMinimized] = useState(false);

  const changeSidebarSize = () => {
    setMinimized(!minimized);
  }
  return (
    <div className="h-full w-3/4 sm:w-2/3 md:w-1/2 lg:w-3/12 xl:w-2/12 flex flex-col ">
      <div className="bg-purple-600 sm:bg-blue-600 md:bg-emerald-600 lg:bg-orange-700 xl:bg-yellow-500 text-white font-semibold h-[5px]">
        a
      </div>
      <div className="flex flex-row justify-between px-2">
        <h1>Titulo</h1>
        <button>
        <span onClick={setMinimized} className={`material-symbols-outlined ${minimized ? 'rotate-[270deg]' : 'rotate-[90deg]'}`}>
            expand_less
          </span>
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
