import React from "react";
import {useNavigate} from "react-router-dom";

const SidebarItem = ({
  icon,
  title,
  link,
  minimized,
  setMinimized,
  activeComponent,
  setActiveComponent,

}) => {

  const navigate = useNavigate();
  return (
    <div>
      <a
        href={link}
        onClick={(event) => {
          event.preventDefault();
          setActiveComponent(title);
          
        }}
        className={`transition-all duration-500 flex flex-row items-center space-x-2 p-2 text-slate-700  font-medium rounded-md cursor-pointer ${
          activeComponent === title
            ? "bg-gray-200 dark:bg-neutral-900 text-blue-600 dark:text-white "
            : "hover:bg-gray-200 hover:text-blue-600 dark:text-neutral-300 dark:hover:bg-neutral-900 duration-50 "
        } ${minimized ? "justify-center" : ""} `}
      >
        
        <span class="material-symbols-rounded">{icon}</span>
        {!minimized ? <span>{title}</span> : null}
      </a>
    </div>
  );
};

export default SidebarItem;
