import React, { useEffect, useState } from 'react'
import axios from 'axios';

const AtividadesComponent = ({ token, handleButtonClick }) => {
  const [atividades, setAtividades] = useState([]);

  useEffect(() => {
    axios.post('http://localhost:5000/api/getatividades', {
      user_id: 'auth0|65999d197d1aba9a56a35600', // Replace with the actual user_id
    }, {
      headers: {
        Authorization: `Bearer ${token}`, // Replace with the actual token
      },
    })
    .then(response => setAtividades(response.data))
    .catch(error => console.error('Error:', error));
  }, [token]);

  return (
    <>
      <button onClick={() => handleButtonClick("menu")}>Back to Menu</button>
      <div>Atividades Component </div>
      <div>
        <h2>Atividades</h2>
        {atividades.map((atividade) => (
          <div key={atividade.id}>
            <h3>{atividade.nome}</h3>
            <p>{atividade.descricao}</p>
          </div>
        ))}
        </div>
    </>
  )
}

export default AtividadesComponent
