import React, { useEffect, useState } from 'react'
import axios from 'axios';

const AtividadesComponent = ({ token, handleButtonClick }) => {
  const [data, setData] = useState(null);
  const [atividades, setAtividades] = useState([]); // Initialize atividades as an empty array

  useEffect(() => {
    const fetchData = async () => {
      if (token) {
        try {
          const response = await axios.post('http://localhost:5000/api/getatividades', {
            user_id: 'auth0|65999d197d1aba9a56a35600', // Replace with the actual user_id
          }, {
            headers: {
              //descobrir porque token variavel nao funciona
              Authorization: `Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IkpXa0taTXBKdFh0cll2M0ZqYk82SyJ9..GXpQqZ-QCSA6jj0VMGkw9s4gMI9jN_RFup39ugKQRTPI4zWCMaxHbSHN5RmBI2oRpzzOqN4QFvPZDrjg39HDUyQ4Bu2_LimsOuz175uW9P0N_RFh2MEO6l5aNLKCARDAMTM1Ah97EEkk1WF7x55fa6qqmoxiCKYWyCCr10x8PDqTt55gJTUfF9Z0sW7dpdDuyHdIp5xC1cK1-POamJP1JYu3pXo6LUcjIVcXNzOBgeYnMPBxeoAtRghLI7iPukiU9ANVuwYSqG49D3_bEfKW37qxVftRS_ddaQNJx3fBTeugA-TGk1jpfJnWrULKO766tsJn6aCOUuEknwpFxQUPKw`, // Use the token here
            },
          });
          setData(response.data);
          setAtividades(response.data); // Set atividades with the response data
        } catch (error) {
          console.error('Error:', error.message);
        }
      }
    };

    if (token) {
      fetchData();
    }
  }, [token]);

  return (
    <>
      <div className='bg-zinc-300 h-full w-full flex flex-col items-start p-5'>
      <div className=''>
        <button onClick={() => handleButtonClick("menu")}>Back to Menu</button>
      </div>
      


      {atividades.map((atividade, index) => (
        <div key={index}>
          <h2>{atividade.name}</h2>
          <p>Date: {atividade.date}</p>
          <p>Number of participants: {atividade.n_participants}</p>
        </div>
      ))}
      </div>
    </>
  )
}

export default AtividadesComponent
