import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import AtividadeComponent from "./AtividadesComponent";
import UsersComponent from "./UsersComponent";
import Menu from "./Menu";
import Profile from "./Profile";
import TokenContext from "../TokenContext";
import Cookies from "js-cookie";
import Sidebar from "./Sidebar";
import Content from "./Content";

const Dashboard = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const token = localStorage.getItem("access_token");
  const [sidebarMinimized, setSidebarMinimized] = useState(false);
  const [activeComponent, setActiveComponent] = useState("Atividades");
  const [colorMode, setColorMode] = useState(Cookies.get("colorMode") || "light");

  useEffect(() => {
    document.cookie = `colorMode=${colorMode}`;
  }, [colorMode]);

  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("/api/protected", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        //console.log("valid");
      })
      .catch((error) => {
        navigate("/login");
      });
  }, []);


  const handleButtonClick = (componentName) => {
    setActiveComponent(componentName);
    //console.log(componentName)
  };

  useEffect(() => {
    //console.log(activeComponent);
  }, [activeComponent])
  return (
    <>
      <div className="h-dvh w-dvw flex bg-zinc-100 ">
        <Sidebar
          minimized={sidebarMinimized}
          setMinimized={setSidebarMinimized}
          activeComponent={activeComponent}
          setActiveComponent={setActiveComponent}
        />
        <div className="w-fit bg-zinc-200">
          <Content
            sidebarMinimized={sidebarMinimized}
            setSidebarMinimized={setSidebarMinimized}
          />
        </div>
      </div>
    </>
  );
};

export default Dashboard;
