import React, { useState } from "react";
import SidebarItem from "./SidebarItem";

const Sidebar = ({ minimized, setMinimized, activeComponent, setActiveComponent }) => {
  const changeSidebarSize = () => {
    console.log(minimized)
    setMinimized(!minimized);
  };
  return (
    <>
      <button
        onClick={changeSidebarSize}
        className={`${
          minimized ? "absolute top-1 left-1" : "hidden"
        } lg:hidden cursor-pointer z-[1]`}
      >
        <span
          
          className="material-symbols-outlined rotate-[90deg]"
        >
          expand_less
        </span>
      </button>
      <div
        className={`bg-zinc-100 absolute z-[1] transition-all duration-500 ease-in-out lg:max-w-[34%] lg:relative h-full w-3/4 sm:w-2/3 md:w-4/12 ${
          minimized ? "hidden lg:flex lg:w-2/12 " : "lg:w-4/12 "
        } `}
      >
        <div className="flex flex-col h-full">
          <div className="bg-purple-600 sm:bg-blue-600 md:bg-emerald-600 lg:bg-orange-700  text-white font-semibold h-[5px]">
            a
          </div>

          <div
            className={`${
              minimized ? "hidden" : "flex"
            } h-full md:flex flex-col space-y-4 transition-all pl-2 duration-500 ease-in-out`}
          >
            <div className="header flex flex-row h-20 w-full justify-between items-center pl-2">
              <img
                src={minimized ? "v_logo_stripped.png" : "h_logo.png"}
                className={`${minimized ? "h-12 " : "h-14 "}`}
              />
              <button onClick={changeSidebarSize} className="h-full bg-transparent md:opacity-0 md:bg-zinc-200 rounded-l-md md:hover:opacity-100 duration-200">
                <span
                // hover, fazer com que desapareca e no hover aparecer
                  className={`material-symbols-outlined transition-all duration-500 ease-in-out ${
                    minimized ? "rotate-[90deg]" : "rotate-[270deg]"
                  }`}
                >
                  expand_less
                </span>
              </button>
            </div>
            <div className="flex flex-col space-y-3 pr-2 justify-center overflow-y-auto">
              <SidebarItem activeComponent={activeComponent} setActiveComponent={setActiveComponent} minimized={minimized} setMinimized={setMinimized} icon="space_dashboard" title="Atividades"/>
              <SidebarItem activeComponent={activeComponent} setActiveComponent={setActiveComponent} minimized={minimized} setMinimized={setMinimized} icon="group" title="Utilizadores"/>
              <SidebarItem activeComponent={activeComponent} setActiveComponent={setActiveComponent} minimized={minimized} setMinimized={setMinimized} icon="settings" title="Definições"/>
            </div>
            <div className="flex-grow flex flex-col justify-end">
              <div className="flex flex-row mb-5 items-center justify-evenly space-x-3 font-semibold">
                <img src="pfp.png" className="w-10 rounded-full" />
                <h1>Mafarrico</h1>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
