import React, { useEffect, useState } from "react";
import axios from "axios";

const AtividadesComponent = ({ token, handleButtonClick }) => {
  const [atividades, setAtividades] = useState([]); // Initialize atividades as an empty array
  const [isLoading, setIsLoading] = useState(false); // Initialize isLoading as false

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);

      if (token) {
        try {
          const response = await axios.post(
            "http://localhost:5000/api/getatividades",
            {
              user_id: "auth0|65999d197d1aba9a56a35600", // Replace with the actual user_id
            },
            {
              headers: {
                //descobrir porque token variavel nao funciona
                Authorization: `Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IkpXa0taTXBKdFh0cll2M0ZqYk82SyJ9..GXpQqZ-QCSA6jj0VMGkw9s4gMI9jN_RFup39ugKQRTPI4zWCMaxHbSHN5RmBI2oRpzzOqN4QFvPZDrjg39HDUyQ4Bu2_LimsOuz175uW9P0N_RFh2MEO6l5aNLKCARDAMTM1Ah97EEkk1WF7x55fa6qqmoxiCKYWyCCr10x8PDqTt55gJTUfF9Z0sW7dpdDuyHdIp5xC1cK1-POamJP1JYu3pXo6LUcjIVcXNzOBgeYnMPBxeoAtRghLI7iPukiU9ANVuwYSqG49D3_bEfKW37qxVftRS_ddaQNJx3fBTeugA-TGk1jpfJnWrULKO766tsJn6aCOUuEknwpFxQUPKw`, // Use the token here
              },
            }
          );

          setAtividades(response.data); // Set atividades with the response data
          setIsLoading(false); // Set isLoading to false
        } catch (error) {
          console.error("Error:", error.message);
        }
      }
    };

    if (token) {
      fetchData();
    }
  }, [token]);

  return (
    <>
      <div className="bg-zinc-300 h-full w-full flex flex-col items-start px-5 py-2 space-y-5">
        <div className="">
          <button onClick={() => handleButtonClick("menu")}>
            Back to Menu
          </button>
        </div>

        <div className=" flex flex-col space-y-3">
          <h2 className="font-bold text-[20px]">Atividades</h2>
          <div className="flex flex-row space-x-2">
            {isLoading ? (
              <div role="status" class="max-w-sm animate-pulse">
                <div class="h-2.5 bg-gray-200 rounded-full dark:bg-gray-700 w-48 mb-4"></div>
                <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 max-w-[360px] mb-2.5"></div>
                <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5"></div>
                <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 max-w-[330px] mb-2.5"></div>
                <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 max-w-[300px] mb-2.5"></div>
                <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 max-w-[360px]"></div>
                <span class="sr-only">Loading...</span>
              </div>
            ) : (
              atividades.map((atividade, index) => (
                <div
                  className="bg-white hover:bg-zinc-100 duration-150 p-3 rounded-md"
                  key={index}
                >
                  <h2>{atividade.name}</h2>
                  <p>Date: {atividade.date}</p>
                  <p>Number of participants: {atividade.n_participants}</p>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default AtividadesComponent;
